declare module '*.svg' {
    const content: any;
    export default content;
  }
  
  declare module '*.png' {
    const content: any;
    export default content;
  }
  
  declare module '*.jpg' {
    const content: any;
    export default content;
  }
  
  declare module '*.module.scss' {
    const styles: { [className: string]: string };
    export default styles;
  }